import mammoth from 'mammoth';
export default {
  data: function data() {
    return {
      vHtml: ''
    };
  },
  mounted: function mounted() {
    document.getElementById('document').addEventListener('change', this.uploading, false);
  },
  methods: {
    uploading: function uploading(event) {
      var that = this;
      var file = event.target.files[0]; // 获取文件

      console.log(file);
      var reader = new FileReader();
      reader.readAsArrayBuffer(file);

      reader.onload = function (e) {
        var buffer = e.target.result; // 此时是arraybuffer类型

        mammoth.convertToHtml({
          arrayBuffer: buffer
        }).then(function (result) {
          console.log(result);
          that.vHtml = result.value;
        }).done();
      };
    }
  }
};
